import React, { Component } from 'react';
import commerce from './lib/commerce'

import Product from './Components/Products/Product';

class App extends Component {
  constructor(props) {
    super(props);

    this.state = {
      CardProduct: [],
      cart: {},
    };
  }

  fetchCart() {
    commerce.cart.retrieve().then((cart) => {
      this.setState({ cart });
    }).catch((error))
  }


  render () {
    return (
      <div className="app">

        <Product />

      </div>
    )
  }
}

export default App;
